int boardsInfoTable_reset_valid(void) {
	struct boardsInfoTable_entry *entry = boardsInfoTable_head;

	if (!entry)
		return 0;

	mutex_lock(lstlock);
	while (entry) {
		entry->valid = 0;
		entry = entry->next;
	}
	mutex_unlock(lstlock);

	return 0;
}

int boardsInfoTable_update(struct nmboard_info *bdinfo) {
	struct boardsInfoTable_entry *entry = boardsInfoTable_head;

	if (!bdinfo)
		return -1;

	mutex_lock(lstlock);
	while (entry) {
		if ((entry->boardRack == bdinfo->rack) && 
				(entry->boardShelf == bdinfo->shelf) && 
				(entry->boardSlot == bdinfo->slot) &&
				(entry->boardSubSlot == bdinfo->subslot)) {
			entry->boardType = bdinfo->bdtype;
			entry->boardStat = bdinfo->stat;
			entry->valid = 1;
			mutex_unlock(lstlock);
			return 0;
		}
		entry = entry->next;
	}

	entry = boardsInfoTable_createEntry(bdinfo->rack, 
			bdinfo->shelf, bdinfo->slot, bdinfo->subslot);
	if (!entry) {
		mutex_unlock(lstlock);
		return -1;
	}
	
	entry->boardType = bdinfo->bdtype;
	entry->boardStat = bdinfo->stat;
	entry->valid = 1;

	mutex_unlock(lstlock);
	return 0;
}

