int boardsInfoTable_getbdtype(struct nm_lkaddr_info po, int *bdtype) {
	struct boardsInfoTable_entry *entry = boardsInfoTable_head;

	if (!bdtype)
		return -1;

	mutex_lock(lstlock);
	while (entry) {
		if ((entry->boardRack == po.rack) && 
				(entry->boardShelf == po.shelf) && 
				(entry->boardSlot == po.slot) &&
				(entry->boardSubSlot == po.subslot)) {
			*bdtype = entry->boardType;
			mutex_unlock(lstlock);
			return 0;
		}
		entry = entry->next;
	}

	mutex_unlock(lstlock);
	return -1;
}

int boardsInfoTable_getboards(int bdtype, struct nm_lkaddr_info *po) {
	struct boardsInfoTable_entry *entry = boardsInfoTable_head;
	int i = 0;

	if (!po)
		return 0;

	mutex_lock(lstlock);
	while (entry) {
		if (entry->boardType == bdtype) {
			po[i].rack = entry->boardRack;
			po[i].shelf = entry->boardShelf;
			po[i].slot = entry->boardSlot;
			po[i].subslot = entry->boardSubSlot;
			i++;
			if (i >= SNMP_MAX_BOARD_CNT) {
				LOGERROR("%s: boards over the max num", __func__, i);
				return 0;
			}
		}
		entry = entry->next;
	}

	mutex_unlock(lstlock);

	return i;
}

