/** handles requests for the boardsInfoTable table */
int
boardsInfoTable_handler(
    netsnmp_mib_handler               *handler,
    netsnmp_handler_registration      *reginfo,
    netsnmp_agent_request_info        *reqinfo,
    netsnmp_request_info              *requests) {

    netsnmp_request_info       *request;
    netsnmp_table_request_info *table_info;
    struct boardsInfoTable_entry          *table_entry;

    DEBUGMSGTL(("boardsInfoTable:handler", "Processing request (%d)\n", reqinfo->mode));

	mutex_lock(lstlock);
    switch (reqinfo->mode) {
        /*
         * Read-support (also covers GetNext requests)
         */
    case MODE_GET:
        for (request=requests; request; request=request->next) {
            table_entry = (struct boardsInfoTable_entry *)
                              netsnmp_extract_iterator_context(request);
            table_info  =     netsnmp_extract_table_info(      request);
    
            if ( !table_entry ) {
                continue;
            }

            switch (table_info->colnum) {
            case COLUMN_BOARDRACK:
                snmp_set_var_typed_integer( request->requestvb, ASN_INTEGER,
                                            table_entry->boardRack);
                break;
            case COLUMN_BOARDSHELF:
                snmp_set_var_typed_integer( request->requestvb, ASN_INTEGER,
                                            table_entry->boardShelf);
                break;
            case COLUMN_BOARDSLOT:
                snmp_set_var_typed_integer( request->requestvb, ASN_INTEGER,
                                            table_entry->boardSlot);
                break;
            case COLUMN_BOARDSUBSLOT:
                snmp_set_var_typed_integer( request->requestvb, ASN_INTEGER,
                                            table_entry->boardSubSlot);
                break;
            case COLUMN_BOARDTYPE:
                snmp_set_var_typed_integer( request->requestvb, ASN_INTEGER,
                                            table_entry->boardType);
                break;
            case COLUMN_BOARDSTAT:
                snmp_set_var_typed_integer( request->requestvb, ASN_INTEGER,
                                            table_entry->boardStat);
                break;
            default:
                netsnmp_set_request_error(reqinfo, request,
                                          SNMP_NOSUCHOBJECT);
                break;
            }
        }
        break;

    }
	mutex_unlock(lstlock);

    return SNMP_ERR_NOERROR;
}

