void e1PhyMibPortInfoAlarmTable_updateEntry(struct e1PhyMibPortInfoAlarmTable_entry *entry, 
		in_addr_t po, int port, int num, nm_port_alarm *alarm, nm_port_stat *stat)
{
	unsigned long int tmp;

	entry->e1PhyMibPortInfoAlarmTablePosition  = po;
	entry->e1PhyMibPortInfoAlarmTablePort	   = port;
	entry->e1PhyMibPortInfoAlarmTableE1Port	   = num;

	tmp = alarm->r;
	entry->e1PhyMibPortInfoAlarmTableLOS	   = (tmp & ALARM_LOS) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableLOF	   = (tmp & ALARM_LOF) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableAIS	   = (tmp & ALARM_AIS) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableRAI	   = (tmp & ALARM_RAI) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableCASMFLOSS = (tmp & ALARM_CASMFLOSS) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableCRCMFLOSS = (tmp & ALARM_CRCMFLOSS) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableCASMFRAI  = (tmp & ALARM_CASMFRAI) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableOEQ       = (tmp & ALARM_OEQ) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableUEQ       = (tmp & ALARM_UEQ) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableJALT      = (tmp & ALARM_JALT) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableRESF      = (tmp & ALARM_RESF) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableRESEM     = (tmp & ALARM_RESEM) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableRSLIP     = (tmp & ALARM_RSLIP) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableRSL       = (tmp & ALARM_RSL_MASK ) >> ALARM_RSL_SHIFT;

	tmp = alarm->t;
	entry->e1PhyMibPortInfoAlarmTableTOC       = (tmp & ALARM_TOC) ? 1 : 0;
	entry->e1PhyMibPortInfoAlarmTableTSC       = (tmp & ALARM_TSC) ? 1 : 0;

	tmp = stat->lcvc;
	entry->e1PhyMibPortInfoAlarmTableCODE      = tmp;
	tmp = stat->pcvc;
	entry->e1PhyMibPortInfoAlarmTableCRC       = tmp;
	tmp = stat->fosc;
	entry->e1PhyMibPortInfoAlarmTableFAS       = tmp;
	tmp = stat->ebc;
	entry->e1PhyMibPortInfoAlarmTableEBIT      = tmp;
}

int e1PhyMibPortInfoAlarmTable_update(struct nm_lkaddr_info po, unsigned char *data) {
  	struct e1PhyMibPortInfoAlarmTable_entry	*entry = NULL;
	nm_port_alarm *al;
	nm_port_stat *st;
	in_addr_t position;	
	int maxport, port, num;
	int phycnt, pcnt;
	unsigned int i, j;
	
	if (e1PhyMibPortInfoAlarmTable_count >= E1PHYMAXITEMS) 
	{
		LOGERROR("E1Alarm:item over the max row %d", E1PHYMAXITEMS);
		return -1;
	}
	
	mutex_lock(lstlock);
	/* 1: delete board e1phyinfo  2: add new data */
	position = (po.rack << 24) | (po.shelf << 16) | (po.slot << 8) | po.subslot;
//	e1PhyMibPortInfoAlarmTable_delete(position);

	phycnt = data[0];
	pcnt = data[1];
	if (phycnt == 16 && pcnt == 8) {
		maxport = 8;
	}
	else {
		LOGERROR("E1PHY alarm stat update phycnt:%d,pcnt:%d wrong data.", phycnt, pcnt);
		return -1;
	}
	al = (nm_port_alarm *)&data[2];
	st = (nm_port_stat *)&data[sizeof(nm_port_alarm)*phycnt*pcnt+2];

	for (i = 0; i < maxport; i++) {
		port = i + 1;

		for(j = 0; j < 16; j++) {
			num = j + 1;
			entry = e1PhyMibPortInfoAlarmTable_findEntry(position, port, num);
			if (entry == NULL) {
				entry = e1PhyMibPortInfoAlarmTable_createEntry(position, port, num);
				if (!entry) {
					LOGERROR("%s: createEntry failed", __func__);
					return -1;
				}
			}
			e1PhyMibPortInfoAlarmTable_updateEntry(entry, position, port, num, al, st);
			al++;
			st++;
		}
    }

	mutex_unlock(lstlock);
	return 0;
}

