void e1PhyMibPortInfoAlarmTable_check_and_clear(void)
{
	struct e1PhyMibPortInfoAlarmTable_entry *entry = e1PhyMibPortInfoAlarmTable_head;
	struct nm_lkaddr_info po[SNMP_MAX_BOARD_CNT];
	int cnt = 0;
	in_addr_t position;
	int i;

	if (!entry)
		return;

	mutex_lock(lstlock);

	/* reset valid */
	while (entry) {
		entry->valid = 0;
		entry = entry->next;
	}

	/* get boards, and set valid */
	memset(po, 0, sizeof(po));
	cnt = boardsInfoTable_getboards(BOARD_TYPE_EIPB_V2, po);
	for (i = 0; i < cnt; i++) {
		position = (po[i].rack << 24) | (po[i].shelf << 16) | (po[i].slot << 8) | po[i].subslot;
		entry = e1PhyMibPortInfoAlarmTable_head;
		while (entry) {
			if (entry->e1PhyMibPortInfoAlarmTablePosition == position) {
				entry->valid = 1;
			}
			entry = entry->next;
		}
	}

	/* clear invalid */
	e1PhyMibPortInfoAlarmTable_head =
		e1PhyMibPortInfoAlarmTable_del_invalid_entrys(e1PhyMibPortInfoAlarmTable_head);

	mutex_unlock(lstlock);
}

void e1PhyMibPortInfoAlarmTable_clear(void)
{

	mutex_lock(lstlock);

	while (e1PhyMibPortInfoAlarmTable_head) {
		e1PhyMibPortInfoAlarmTable_removeEntry(e1PhyMibPortInfoAlarmTable_head);
	}

	e1PhyMibPortInfoAlarmTable_head = NULL;
	e1PhyMibPortInfoAlarmTable_count = 0;

	mutex_unlock(lstlock);
}


